        List&lt;Integer&gt; numbers = Arrays.asList(1, 2, 3, 4, 5);

        // findFirst
        Optional&lt;Integer&gt; first = numbers.stream()
            .filter(n -&gt; n &gt; 2)
            .findFirst();
        System.out.println("第一個&gt;2: " + first.orElse(-1));

        // findAny
        Optional&lt;Integer&gt; any = numbers.parallelStream()
            .filter(n -&gt; n &gt; 2)
            .findAny();
        System.out.println("任一個&gt;2: " + any.orElse(-1));

        // anyMatch
        boolean hasEven = numbers.stream()
            .anyMatch(n -&gt; n % 2 == 0);
        System.out.println("有偶數: " + hasEven);

        // allMatch
        boolean allPositive = numbers.stream()
            .allMatch(n -&gt; n &gt; 0);
        System.out.println("都是正數: " + allPositive);

        // noneMatch
        boolean noNegative = numbers.stream()
            .noneMatch(n -&gt; n &lt; 0);
        System.out.println("沒有負數: " + noNegative);
    }
}
