        String joinedWithPrefix = words.stream()
            .collect(Collectors.joining(", ", "[", "]"));
        System.out.println("帶前後綴: " + joinedWithPrefix);

        // 統計
        IntSummaryStatistics lengthStats = words.stream()
            .collect(Collectors.summarizingInt(String::length));
        System.out.println("長度統計: " + lengthStats);

        // 分組進階
        Map&lt;Character, List&lt;String&gt;&gt; groupedByFirst = words.stream()
            .collect(Collectors.groupingBy(w -&gt; w.charAt(0)));
        System.out.println("依首字母分組: " + groupedByFirst);

        // 分組後計算
        Map&lt;Character, Long&gt; countByFirst = words.stream()
            .collect(Collectors.groupingBy(
                w -&gt; w.charAt(0),
                Collectors.counting()
            ));
        System.out.println("首字母計數: " + countByFirst);

        // 多層分組
        Map&lt;Character, Map&lt;Integer, List&lt;String&gt;&gt;&gt; multiLevel = words.stream()
            .collect(Collectors.groupingBy(
                w -&gt; w.charAt(0),
                Collectors.groupingBy(String::length)
            ));
        System.out.println("多層分組: " + multiLevel);
    }
}
