Java<pre><code>public class CustomCollector {
    public static void main(String[] args) {
        List&lt;String&gt; words = Arrays.asList("apple", "banana", "cherry");

        // 使用自定義收集器
        String result = words.stream()
            .collect(toStringWithBrackets());
        System.out.println("自定義收集: " + result);
    }

    public static Collector&lt;String, ?, String&gt; toStringWithBrackets() {
        return Collector.of(
            StringBuilder::new,                    // supplier
            (sb, s) -&gt; sb.append("[").append(s).append("]"), // accumulator
            (sb1, sb2) -&gt; sb1.append(sb2),        // combiner
            StringBuilder::toString               // finisher
        );
    }
}
