Java<pre><code>public class DataProcessingPipeline {
    static class Employee {
        private String name;
        private String department;
        private int age;
        private double salary;

        public Employee(String name, String department, int age, double salary) {
            this.name = name;
            this.department = department;
            this.age = age;
            this.salary = salary;
        }

        // getters
        public String getName() { return name; }
        public String getDepartment() { return department; }
        public int getAge() { return age; }
        public double getSalary() { return salary; }

        @Override
        public String toString() {
            return String.format("Employee{name='%s', dept='%s', age=%d, salary=%.0f}", 
                name, department, age, salary);
        }
    }

    public static void main(String[] args) {
        List&lt;Employee&gt; employees = Arrays.asList(
            new Employee("Alice", "IT", 28, 75000),
            new Employee("Bob", "IT", 32, 85000),
            new Employee("Charlie", "HR", 25, 55000),
            new Employee("Diana", "IT", 29, 78000),
            new Employee("Eve", "Finance", 35, 90000),
            new Employee("Frank", "HR", 27, 58000)
        );

        // 複雜查詢：IT 部門年齡 &gt; 27 的員工，按薪水排序
        List&lt;Employee&gt; itEmployees = employees.stream()
            .filter(emp -&gt; "IT".equals(emp.getDepartment()))
            .filter(emp -&gt; emp.getAge() &gt; 27)
            .sorted(Comparator.comparing(Employee::getSalary).reversed())
            .collect(Collectors.toList());

        System.out.println("IT部門資深員工:");
        itEmployees.forEach(System.out::println);

        // 統計分析
        Map&lt;String, Double&gt; avgSalaryByDept = employees.stream()
            .collect(Collectors.groupingBy(
                Employee::getDepartment,
                Collectors.averagingDouble(Employee::getSalary)
            ));

        System.out.println("\n各部門平均薪資:");
        avgSalaryByDept.forEach((dept, avg) -&gt; 
            System.out.printf("%s: %.0f\n", dept, avg));

        // 最高薪員工
        Optional&lt;Employee&gt; highestPaid = employees.stream()
            .max(Comparator.comparing(Employee::getSalary));

        highestPaid.ifPresent(emp -&gt; 
            System.out.println("\n最高薪員工: " + emp));

        // 部門統計
        Map&lt;String, IntSummaryStatistics&gt; ageStatsByDept = employees.stream()
            .collect(Collectors.groupingBy(
                Employee::getDepartment,
                Collectors.summarizingInt(Employee::getAge)
            ));

        System.out.println("\n各部門年齡統計:");
        ageStatsByDept.forEach((dept, stats) -&gt; 
            System.out.printf("%s: 平均%.1f歲, 最大%d歲, 最小%d歲\n", 
                dept, stats.getAverage(), stats.getMax(), stats.getMin()));
    }
}
