// 使用自定義例外的範例
public class CustomExceptionExample {
    static class BankAccount {
        private double balance;
        private String accountNumber;

        public BankAccount(String accountNumber, double balance) {
            this.accountNumber = accountNumber;
            this.balance = balance;
        }

        public void withdraw(double amount) throws InsufficientFundsException, ValidationException {
            validateAmount(amount);

            if (amount &gt; balance) {
                throw new InsufficientFundsException(amount, balance);
            }

            balance -= amount;
            System.out.printf("成功提取 %.2f，餘額: %.2f\n", amount, balance);
        }

        private void validateAmount(double amount) throws ValidationException {
            if (amount &lt;= 0) {
                throw new ValidationException("amount", amount, "金額必須大於 0");
            }
            if (amount &gt; 10000) {
                throw new ValidationException("amount", amount, "單次提取不能超過 10000");
            }
        }

        public double getBalance() { return balance; }
    }

    public static void main(String[] args) {
        BankAccount account = new BankAccount("12345", 1000.0);

        try {
            account.withdraw(500);    // 成功
            account.withdraw(600);    // 餘額不足
        } catch (InsufficientFundsException e) {
            System.out.println("提取失敗: " + e.getMessage());
            System.out.printf("缺少金額: %.2f\n", e.getShortfall());
        } catch (ValidationException e) {
            System.out.println("驗證失敗: " + e.getMessage());
            System.out.println("問題欄位: " + e.getField());
        }

        try {
            account.withdraw(-100);   // 驗證失敗
        } catch (InsufficientFundsException e) {
            System.out.println("餘額不足: " + e.getMessage());
        } catch (ValidationException e) {
            System.out.println("驗證錯誤: " + e.getMessage());
        }
    }
}
