Java<pre><code>import java.io.*;
import java.util.Arrays;

public class DebuggingTechniques {
    public static void main(String[] args) {
        try {
            problematicMethod();
        } catch (Exception e) {
            // 1. 基本例外資訊
            System.out.println("例外類型: " + e.getClass().getSimpleName());
            System.out.println("例外訊息: " + e.getMessage());

            // 2. 堆疊追蹤
            System.out.println("\n堆疊追蹤:");
            e.printStackTrace();

            // 3. 取得堆疊追蹤元素
            System.out.println("\n詳細堆疊資訊:");
            StackTraceElement[] stackTrace = e.getStackTrace();
            for (StackTraceElement element : stackTrace) {
                System.out.printf("  %s.%s(%s:%d)\n", 
                    element.getClassName(), 
                    element.getMethodName(),
                    element.getFileName(), 
                    element.getLineNumber());
            }

            // 4. 根本原因
            Throwable rootCause = getRootCause(e);
            System.out.println("\n根本原因: " + rootCause.getMessage());
        }
    }

    public static void problematicMethod() throws IOException {
        try {
            deepMethod();
        } catch (RuntimeException e) {
            throw new IOException("IO 操作失敗", e);
        }
    }

    public static void deepMethod() {
        try {
            int result = 1 / 0;
        } catch (ArithmeticException e) {
            throw new RuntimeException("計算錯誤", e);
        }
    }

    // 取得根本原因的工具方法
    public static Throwable getRootCause(Throwable throwable) {
        Throwable cause = throwable;
        while (cause.getCause() != null &amp;&amp; cause.getCause() != cause) {
            cause = cause.getCause();
        }
        return cause;
    }
}
</code></pre>

