public class LoggingExample {
    private static final Logger logger = Logger.getLogger(LoggingExample.class.getName());

    static {
        // 設定日誌格式
        ConsoleHandler handler = new ConsoleHandler();
        handler.setFormatter(new SimpleFormatter() {
            @Override
            public String format(LogRecord record) {
                return String.format("[%s] %s: %s - %s%n",
                    record.getLevel(),
                    LocalDateTime.now().format(DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm:ss")),
                    record.getLoggerName(),
                    record.getMessage()
                );
            }
        });

        logger.setUseParentHandlers(false);
        logger.addHandler(handler);
        logger.setLevel(Level.ALL);
    }

    public static void main(String[] args) {
        try {
            processData("valid_data");
            processData(null);  // 會引發例外
        } catch (Exception e) {
            // 記錄例外，包含堆疊追蹤
            logger.log(Level.SEVERE, "處理資料時發生錯誤", e);
        }
    }

    public static void processData(String data) {
        logger.info("開始處理資料: " + data);

        try {
            if (data == null) {
                logger.warning("接收到 null 資料");
                throw new IllegalArgumentException("資料不能為 null");
            }

            // 模擬處理過程
            logger.fine("資料驗證通過");
            String result = data.toUpperCase();
            logger.info("處理完成，結果: " + result);

        } catch (Exception e) {
            logger.log(Level.SEVERE, "資料處理失敗", e);
            throw e;  // 重新拋出例外
        }
    }
}
