Java<pre><code>public class AssertionExample {
    public static void main(String[] args) {
        // 注意：需要使用 -ea 參數啟用斷言
        // java -ea AssertionExample

        int age = 25;

        // 簡單斷言
        assert age &gt;= 0 : "年齡不能為負數";

        // 複雜條件斷言
        assert isValidAge(age) : "年齡驗證失敗: " + age;

        try {
            calculateDiscount(-5);  // 會觸發斷言
        } catch (AssertionError e) {
            System.out.println("斷言失敗: " + e.getMessage());
        }

        // 在方法中使用斷言
        String result = processString("Hello");
        assert result != null &amp;&amp; !result.isEmpty() : "處理結果不能為空";

        System.out.println("所有斷言通過");
    }

    public static boolean isValidAge(int age) {
        return age &gt;= 0 &amp;&amp; age &lt;= 150;
    }

    public static double calculateDiscount(int age) {
        // 前置條件斷言
        assert age &gt;= 0 : "年齡不能為負數: " + age;

        double discount = 0.0;
        if (age &lt; 18) {
            discount = 0.1;
        } else if (age &gt;= 65) {
            discount = 0.15;
        }

        // 後置條件斷言
        assert discount &gt;= 0 &amp;&amp; discount &lt;= 1.0 : "折扣必須在 0-1 之間: " + discount;

        return discount;
    }

    public static String processString(String input) {
        // 內部不變量斷言
        assert input != null : "輸入不能為 null";

        String result = input.trim().toUpperCase();

        assert result.length() &lt;= input.length() : "處理後長度不能超過原始長度";

        return result;
    }
}
