Java<pre><code>import java.io.*;
import java.nio.file.*;

public class ExceptionBestPractices {

    // ✅ 好的做法：具體的例外處理
    public static String readConfigFile(String filename) throws IOException {
        if (filename == null || filename.trim().isEmpty()) {
            throw new IllegalArgumentException("檔名不能為空");
        }

        Path path = Paths.get(filename);
        if (!Files.exists(path)) {
            throw new FileNotFoundException("設定檔不存在: " + filename);
        }

        try {
            return Files.readString(path);
        } catch (IOException e) {
            // 記錄日誌，然後重新拋出
            System.err.println("讀取設定檔失敗: " + filename);
            throw new IOException("無法讀取設定檔: " + filename, e);
        }
    }

    // ❌ 不好的做法：捕獲但忽略例外
    public static void badExample1() {
        try {
            int result = 10 / 0;
        } catch (Exception e) {
            // 什麼都不做 - 絕對不要這樣做！
        }
    }

    // ❌ 不好的做法：捕獲太廣泛的例外
    public static void badExample2() {
        try {
            readConfigFile("config.txt");
        } catch (Exception e) {  // 太廣泛
            System.out.println("發生錯誤");
        }
    }

    // ✅ 好的做法：記錄並處理
    public static void goodExample() {
        try {
            String config = readConfigFile("app.properties");
            System.out.println("載入設定: " + config);
        } catch (IllegalArgumentException e) {
            System.err.println("參數錯誤: " + e.getMessage());
        } catch (FileNotFoundException e) {
            System.err.println("檔案不存在，使用預設設定");
            // 使用預設設定的邏輯
        } catch (IOException e) {
            System.err.println("IO 錯誤: " + e.getMessage());
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        goodExample();
    }
}
