Java<pre><code>import java.util.*;

public class BuiltInAnnotations {

    // @Override - 表示方法覆寫
    @Override
    public String toString() {
        return "BuiltInAnnotations Example";
    }

    // @Deprecated - 標記為過時
    @Deprecated
    public void oldMethod() {
        System.out.println("這個方法已過時，不建議使用");
    }

    // @SuppressWarnings - 抑制編譯器警告
    @SuppressWarnings("unchecked")
    public void uncheckedOperation() {
        List list = new ArrayList();  // 原始型別
        list.add("item");             // 未檢查的操作
    }

    // 多重抑制警告
    @SuppressWarnings({"deprecation", "unchecked", "rawtypes"})
    public void multipleSuppress() {
        oldMethod();                  // 使用過時方法
        List list = new ArrayList();  // 原始型別
    }

    // @SafeVarargs - 表示可變參數方法是安全的
    @SafeVarargs
    public final void safeVarargs(List&lt;String&gt;... lists) {
        for (List&lt;String&gt; list : lists) {
            list.forEach(System.out::println);
        }
    }

    // @FunctionalInterface - 標記函數式介面
    @FunctionalInterface
    interface Calculator {
        int calculate(int a, int b);

        // 函數式介面只能有一個抽象方法
        // int anotherMethod(int x); // 這會導致編譯錯誤

        // 但可以有預設方法和靜態方法
        default void printResult(int result) {
            System.out.println("結果: " + result);
        }

        static Calculator getAdder() {
            return (a, b) -&gt; a + b;
        }
    }

    public static void main(String[] args) {
        BuiltInAnnotations example = new BuiltInAnnotations();

        // 使用過時方法會有編譯器警告
        example.oldMethod();

        // 函數式介面使用
        Calculator adder = Calculator.getAdder();
        int result = adder.calculate(10, 20);
        adder.printResult(result);

        // Lambda 表達式
        Calculator multiplier = (x, y) -&gt; x * y;
        multiplier.printResult(multiplier.calculate(5, 6));
    }
}
