Java<pre><code>import java.lang.annotation.*;
import java.lang.reflect.*;
import java.util.*;

// 自定義註解
@Retention(RetentionPolicy.RUNTIME)
@Target({ElementType.TYPE, ElementType.METHOD, ElementType.FIELD})
@interface Service {
    String name();
    int priority() default 1;
    String[] dependencies() default {};
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.METHOD)
@interface Cacheable {
    int timeout() default 300;
    String key() default "";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.METHOD)
@interface Validate {
    String[] rules();
}

// 被註解的類別
@Service(name = "UserService", priority = 5, dependencies = {"database", "cache"})
public class AnnotationProcessingExample {

    @Service(name = "userRepository")
    private String repository;

    @Cacheable(timeout = 600, key = "user-${id}")
    @Validate(rules = {"notNull", "validId"})
    public User getUser(Long id) {
        System.out.println("取得使用者: " + id);
        return new User(id, "User" + id);
    }

    @Cacheable(timeout = 300)
    public List&lt;User&gt; getAllUsers() {
        System.out.println("取得所有使用者");
        return Arrays.asList(
            new User(1L, "User1"),
            new User(2L, "User2")
        );
    }

    // 註解處理器
    public static void analyzeAnnotations(Class&lt;?&gt; clazz) {
        System.out.println("分析類別: " + clazz.getName());

        // 分析類別層級註解
        analyzeClassAnnotations(clazz);

        // 分析欄位註解
        analyzeFieldAnnotations(clazz);

        // 分析方法註解
        analyzeMethodAnnotations(clazz);
    }

    private static void analyzeClassAnnotations(Class&lt;?&gt; clazz) {
        System.out.println("\n=== 類別註解 ===");

        Service service = clazz.getAnnotation(Service.class);
        if (service != null) {
            System.out.println("服務名稱: " + service.name());
            System.out.println("優先級: " + service.priority());
            System.out.println("依賴: " + Arrays.toString(service.dependencies()));
        }
    }

    private static void analyzeFieldAnnotations(Class&lt;?&gt; clazz) {
        System.out.println("\n=== 欄位註解 ===");

        Field[] fields = clazz.getDeclaredFields();
        for (Field field : fields) {
            System.out.println("欄位: " + field.getName());

            Service service = field.getAnnotation(Service.class);
            if (service != null) {
                System.out.println("  @Service: " + service.name());
            }
        }
    }

    private static void analyzeMethodAnnotations(Class&lt;?&gt; clazz) {
        System.out.println("\n=== 方法註解 ===");

        Method[] methods = clazz.getDeclaredMethods();
        for (Method method : methods) {
            if (method.getAnnotations().length &gt; 0) {
                System.out.println("方法: " + method.getName());

                // 檢查 Cacheable 註解
                Cacheable cacheable = method.getAnnotation(Cacheable.class);
                if (cacheable != null) {
                    System.out.println("  @Cacheable:");
                    System.out.println("    timeout: " + cacheable.timeout());
                    System.out.println("    key: " + cacheable.key());
                }

                // 檢查 Validate 註解
                Validate validate = method.getAnnotation(Validate.class);
                if (validate != null) {
                    System.out.println("  @Validate:");
                    System.out.println("    rules: " + Arrays.toString(validate.rules()));
                }
            }
        }
    }

    public static void main(String[] args) {
        analyzeAnnotations(AnnotationProcessingExample.class);

        // 動態調用帶註解的方法
        System.out.println("\n=== 動態調用 ===");
        try {
            AnnotationProcessingExample instance = new AnnotationProcessingExample();
            Method getUserMethod = instance.getClass().getMethod("getUser", Long.class);

            // 檢查註解並調用
            if (getUserMethod.isAnnotationPresent(Cacheable.class)) {
                System.out.println("方法有快取註解，執行快取邏輯...");
            }

            User result = (User) getUserMethod.invoke(instance, 123L);
            System.out.println("調用結果: " + result);

        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}

