Java<pre><code>import java.lang.annotation.*;
import java.lang.reflect.*;
import java.util.*;
import java.util.concurrent.ConcurrentHashMap;

// 配置相關註解
@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.TYPE)
@interface Configuration {
    String profile() default "default";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.METHOD)
@interface Bean {
    String name() default "";
    boolean singleton() default true;
}

@Retention(RetentionPolicy.RUNTIME)
@Target({ElementType.FIELD, ElementType.PARAMETER})
@interface Inject {
    String value() default "";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.FIELD)
@interface Value {
    String value();
}

// 簡單的依賴注入容器
class SimpleContainer {
    private final Map&lt;String, Object&gt; beans = new ConcurrentHashMap&lt;&gt;();
    private final Map&lt;String, Method&gt; beanMethods = new ConcurrentHashMap&lt;&gt;();
    private final Properties config = new Properties();

    public SimpleContainer() {
        // 初始化配置
        config.setProperty("app.name", "MyApp");
        config.setProperty("app.version", "1.0");
        config.setProperty("database.url", "jdbc:h2:mem:test");
    }

    public void register(Class&lt;?&gt; configClass) {
        try {
            Configuration configuration = configClass.getAnnotation(Configuration.class);
            if (configuration == null) {
                throw new IllegalArgumentException("類別必須有 @Configuration 註解");
            }

            Object configInstance = configClass.getDeclaredConstructor().newInstance();

            // 處理 @Bean 方法
            Method[] methods = configClass.getDeclaredMethods();
            for (Method method : methods) {
                Bean bean = method.getAnnotation(Bean.class);
                if (bean != null) {
                    String beanName = bean.name().isEmpty() ? 
                        method.getName() : bean.name();

                    beanMethods.put(beanName, method);

                    if (bean.singleton()) {
                        Object beanInstance = method.invoke(configInstance);
                        beans.put(beanName, beanInstance);
                    }
                }
            }

        } catch (Exception e) {
            throw new RuntimeException("註冊配置失敗", e);
        }
    }

    @SuppressWarnings("unchecked")
    public &lt;T&gt; T getBean(String name, Class&lt;T&gt; type) {
        Object bean = beans.get(name);
        if (bean == null &amp;&amp; beanMethods.containsKey(name)) {
            // 延遲創建
            throw new RuntimeException("延遲創建尚未實作");
        }
        return (T) bean;
    }

    public void injectDependencies(Object instance) {
        Class&lt;?&gt; clazz = instance.getClass();

        // 注入欄位
        Field[] fields = clazz.getDeclaredFields();
        for (Field field : fields) {
            try {
                field.setAccessible(true);

                // @Inject 依賴注入
                Inject inject = field.getAnnotation(Inject.class);
                if (inject != null) {
                    String beanName = inject.value().isEmpty() ? 
                        field.getName() : inject.value();
                    Object dependency = beans.get(beanName);
                    if (dependency != null) {
                        field.set(instance, dependency);
                    }
                }

                // @Value 值注入
                Value value = field.getAnnotation(Value.class);
                if (value != null) {
                    String configValue = resolveValue(value.value());
                    Object convertedValue = convertValue(configValue, field.getType());
                    field.set(instance, convertedValue);
                }

            } catch (Exception e) {
                throw new RuntimeException("依賴注入失敗: " + field.getName(), e);
            }
        }
    }

    private String resolveValue(String expression) {
        if (expression.startsWith("${") &amp;&amp; expression.endsWith("}")) {
            String key = expression.substring(2, expression.length() - 1);
            return config.getProperty(key, "");
        }
        return expression;
    }

    private Object convertValue(String value, Class&lt;?&gt; type) {
        if (type == String.class) return value;
        if (type == int.class || type == Integer.class) return Integer.parseInt(value);
        if (type == boolean.class || type == Boolean.class) return Boolean.parseBoolean(value);
        return value;
    }
}

