Java<pre><code>import java.lang.annotation.*;
import java.lang.reflect.*;
import java.util.*;
import java.util.regex.Pattern;

// 驗證註解
@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.FIELD)
@interface NotNull {
    String message() default "欄位不能為空";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.FIELD)
@interface Size {
    int min() default 0;
    int max() default Integer.MAX_VALUE;
    String message() default "長度不符合要求";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.FIELD)
@interface Email {
    String message() default "電子郵件格式不正確";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.FIELD)
@interface Min {
    int value();
    String message() default "值太小";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.FIELD)
@interface Max {
    int value();
    String message() default "值太大";
}

// 驗證結果
class ValidationResult {
    private boolean valid = true;
    private List&lt;String&gt; errors = new ArrayList&lt;&gt;();

    public void addError(String error) {
        this.valid = false;
        this.errors.add(error);
    }

    public boolean isValid() { return valid; }
    public List&lt;String&gt; getErrors() { return errors; }

    @Override
    public String toString() {
        if (valid) return "驗證通過";
        return "驗證失敗: " + String.join(", ", errors);
    }
}

// 驗證器
class Validator {
    private static final Pattern EMAIL_PATTERN = 
        Pattern.compile("^[A-Za-z0-9+_.-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}$");

    public static ValidationResult validate(Object obj) {
        ValidationResult result = new ValidationResult();
        Class&lt;?&gt; clazz = obj.getClass();

        Field[] fields = clazz.getDeclaredFields();
        for (Field field : fields) {
            field.setAccessible(true);

            try {
                Object value = field.get(obj);
                validateField(field, value, result);
            } catch (IllegalAccessException e) {
                result.addError("無法存取欄位: " + field.getName());
            }
        }

        return result;
    }

    private static void validateField(Field field, Object value, ValidationResult result) {
        // @NotNull 驗證
        NotNull notNull = field.getAnnotation(NotNull.class);
        if (notNull != null &amp;&amp; value == null) {
            result.addError(field.getName() + ": " + notNull.message());
            return; // 如果為 null，跳過其他驗證
        }

        if (value == null) return; // 如果沒有 @NotNull 且值為 null，跳過其他驗證

        // @Size 驗證
        Size size = field.getAnnotation(Size.class);
        if (size != null &amp;&amp; value instanceof String) {
            String str = (String) value;
            if (str.length() &lt; size.min() || str.length() &gt; size.max()) {
                result.addError(field.getName() + ": " + size.message() + 
                    String.format(" (需要 %d-%d 個字元，實際 %d 個)", 
                    size.min(), size.max(), str.length()));
            }
        }

        // @Email 驗證
        Email email = field.getAnnotation(Email.class);
        if (email != null &amp;&amp; value instanceof String) {
            String str = (String) value;
            if (!EMAIL_PATTERN.matcher(str).matches()) {
                result.addError(field.getName() + ": " + email.message());
            }
        }

        // @Min 驗證
        Min min = field.getAnnotation(Min.class);
        if (min != null &amp;&amp; value instanceof Number) {
            int intValue = ((Number) value).intValue();
            if (intValue &lt; min.value()) {
                result.addError(field.getName() + ": " + min.message() + 
                    String.format(" (最小值: %d，實際值: %d)", min.value(), intValue));
            }
        }

        // @Max 驗證
        Max max = field.getAnnotation(Max.class);
        if (max != null &amp;&amp; value instanceof Number) {
            int intValue = ((Number) value).intValue();
            if (intValue &gt; max.value()) {
                result.addError(field.getName() + ": " + max.message() + 
                    String.format(" (最大值: %d，實際值: %d)", max.value(), intValue));
            }
        }
    }
}

// 使用驗證註解的類別
class RegisterRequest {
    @NotNull(message = "使用者名稱不能為空")
    @Size(min = 3, max = 20, message = "使用者名稱長度必須在 3-20 個字元之間")
    private String username;

    @NotNull(message = "電子郵件不能為空")
    @Email(message = "請輸入有效的電子郵件地址")
    private String email;

    @NotNull(message = "密碼不能為空")
    @Size(min = 8, message = "密碼至少需要 8 個字元")
    private String password;

    @NotNull(message = "年齡不能為空")
    @Min(value = 18, message = "年齡必須大於等於 18 歲")
    @Max(value = 100, message = "年齡必須小於等於 100 歲")
    private Integer age;

    // 建構子
    public RegisterRequest(String username, String email, String password, Integer age) {
        this.username = username;
        this.email = email;
        this.password = password;
        this.age = age;
    }

    // Getters
    public String getUsername() { return username; }
    public String getEmail() { return email; }
    public String getPassword() { return password; }
    public Integer getAge() { return age; }
}

// 測試驗證框架
public class ValidationTest {
    public static void main(String[] args) {
        // 測試有效資料
        RegisterRequest validRequest = new RegisterRequest(
            "john_doe", "john@example.com", "password123", 25);
        ValidationResult validResult = Validator.validate(validRequest);
        System.out.println("有效資料驗證: " + validResult);

        // 測試無效資料
        RegisterRequest invalidRequest = new RegisterRequest(
            "jo", "invalid-email", "123", 15);
        ValidationResult invalidResult = Validator.validate(invalidRequest);
        System.out.println("\n無效資料驗證: " + invalidResult);

        // 測試 null 資料
        RegisterRequest nullRequest = new RegisterRequest(
            null, null, null, null);
        ValidationResult nullResult = Validator.validate(nullRequest);
        System.out.println("\nNull 資料驗證: " + nullResult);

        // 列出所有錯誤
        if (!invalidResult.isValid()) {
            System.out.println("\n詳細錯誤:");
            invalidResult.getErrors().forEach(error -&gt; 
                System.out.println("- " + error));
        }
    }
}
