        }
        System.out.println();

        // 巢狀迴圈 - 九九乘法表
        System.out.println("\n九九乘法表:");
        for (int i = 1; i &lt;= 9; i++) {
            for (int j = 1; j &lt;= 9; j++) {
                System.out.printf("%d×%d=%2d  ", i, j, i * j);
            }
            System.out.println();
        }

