        demonstrateSQLInjection();
        demonstrateSecureQuery();
    }

    private static void setupDatabase() {
        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD);
             Statement stmt = conn.createStatement()) {

            // 建立使用者表格
            stmt.executeUpdate("""
                CREATE TABLE users (
                    id BIGINT AUTO_INCREMENT PRIMARY KEY,
                    username VARCHAR(50) NOT NULL,
                    password VARCHAR(100) NOT NULL,
                    role VARCHAR(20) DEFAULT 'USER'
                )
            """);

            // 插入測試資料
            stmt.executeUpdate("""
                INSERT INTO users (username, password, role) VALUES 
                ('admin', 'admin123', 'ADMIN'),
                ('user1', 'password1', 'USER'),
                ('user2', 'password2', 'USER')
            """);

            System.out.println("資料庫設置完成");

        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    // ❌ 危險：容易受到 SQL 注入攻擊
    private static void demonstrateSQLInjection() {
        System.out.println("\n=== SQL 注入風險示例 ===");

        // 模擬惡意輸入
        String maliciousInput = "admin'; DROP TABLE users; --";

        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD);
             Statement stmt = conn.createStatement()) {

            // 危險的字串拼接
            String dangerousSQL = "SELECT * FROM users WHERE username = '" + maliciousInput + "'";
            System.out.println("危險 SQL: " + dangerousSQL);

            try {
                ResultSet rs = stmt.executeQuery(dangerousSQL);
                // 這可能導致 SQL 注入攻擊
            } catch (SQLException e) {
                System.out.println("SQL 錯誤（這就是為什麼需要防護）: " + e.getMessage());
            }

        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    // ✅ 安全：使用 PreparedStatement
    private static void demonstrateSecureQuery() {
        System.out.println("\n=== 安全查詢示例 ===");

        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {

            // 安全的參數化查詢
            String secureSQL = "SELECT id, username, role FROM users WHERE username = ? AND password = ?";

            try (PreparedStatement pstmt = conn.prepareStatement(secureSQL)) {
                // 測試正常登入
                System.out.println("測試正常登入:");
                authenticateUser(pstmt, "admin", "admin123");

                // 測試錯誤密碼
                System.out.println("\n測試錯誤密碼:");
                authenticateUser(pstmt, "admin", "wrongpassword");

                // 測試惡意輸入（會被安全處理）
                System.out.println("\n測試惡意輸入:");
                authenticateUser(pstmt, "admin'; DROP TABLE users; --", "anything");
            }

        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    private static void authenticateUser(PreparedStatement pstmt, String username, String password) {
        try {
            pstmt.setString(1, username);
            pstmt.setString(2, password);

            try (ResultSet rs = pstmt.executeQuery()) {
                if (rs.next()) {
                    System.out.printf("登入成功: %s (角色: %s)%n", 
                        rs.getString("username"), rs.getString("role"));
                } else {
                    System.out.println("登入失敗: 使用者名稱或密碼錯誤");
                }
            }
        } catch (SQLException e) {
            System.out.println("查詢錯誤: " + e.getMessage());
        }
    }
}

// 安全的資料庫操作類別
class SecureUserDAO {
    private final String dbUrl;
    private final String dbUser;
    private final String dbPassword;

    public SecureUserDAO(String dbUrl, String dbUser, String dbPassword) {
        this.dbUrl = dbUrl;
        this.dbUser = dbUser;
        this.dbPassword = dbPassword;
    }

    public User findByUsername(String username) throws SQLException {
        String sql = "SELECT id, username, email, role FROM users WHERE username = ?";

        try (Connection conn = DriverManager.getConnection(dbUrl, dbUser, dbPassword);
             PreparedStatement pstmt = conn.prepareStatement(sql)) {

            pstmt.setString(1, username);

            try (ResultSet rs = pstmt.executeQuery()) {
                if (rs.next()) {
                    return new User(
                        rs.getLong("id"),
                        rs.getString("username"),
                        rs.getString("email"),
                        rs.getString("role")
                    );
                }
                return null;
            }
        }
    }

    public boolean createUser(User user, String password) throws SQLException {
        String sql = "INSERT INTO users (username, email, password, role) VALUES (?, ?, ?, ?)";

        try (Connection conn = DriverManager.getConnection(dbUrl, dbUser, dbPassword);
             PreparedStatement pstmt = conn.prepareStatement(sql, Statement.RETURN_GENERATED_KEYS)) {

            pstmt.setString(1, user.getUsername());
            pstmt.setString(2, user.getEmail());
            pstmt.setString(3, hashPassword(password)); // 實際應用中應該加密密碼
            pstmt.setString(4, user.getRole());

            int affectedRows = pstmt.executeUpdate();

            if (affectedRows &gt; 0) {
                try (ResultSet generatedKeys = pstmt.getGeneratedKeys()) {
                    if (generatedKeys.next()) {
                        user.setId(generatedKeys.getLong(1));
                        return true;
                    }
                }
            }
            return false;
        }
    }

    private String hashPassword(String password) {
        // 簡化實作，實際應用應使用 BCrypt 或類似的安全哈希函數
        return "hashed_" + password;
    }
}

