Java<pre><code>import java.sql.*;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class TransactionIsolationDemo {
    private static final String DB_URL = "jdbc:h2:mem:isolation_demo";
    private static final String DB_USER = "sa";
    private static final String DB_PASSWORD = "";

    public static void main(String[] args) {
        setupDatabase();
        demonstrateIsolationLevels();
    }

    private static void setupDatabase() {
        try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD);
             Statement stmt = conn.createStatement()) {

            stmt.executeUpdate("""
                CREATE TABLE inventory (
                    id INT PRIMARY KEY,
                    product_name VARCHAR(50),
                    quantity INT
                )
            """);

            stmt.executeUpdate("""
                INSERT INTO inventory VALUES 
                (1, '筆記型電腦', 10),
                (2, '手機', 25),
                (3, '平板', 15)
            """);

        } catch (SQLException e) {
            e.printStackTrace();
        }
    }

    private static void demonstrateIsolationLevels() {
        System.out.println("=== 交易隔離等級示例 ===\n");

        // 示例 1: READ_UNCOMMITTED - 可能會讀到未提交的資料 (髒讀)
        demonstrateDirtyRead();

        // 示例 2: READ_COMMITTED - 避免髒讀，但可能會不可重複讀
        demonstrateNonRepeatableRead();

        // 示例 3: REPEATABLE_READ - 避免不可重複讀，但可能會幻象讀
        demonstratePhantomRead();

        // 示例 4: SERIALIZABLE - 避免所有問題，但效能最低
        demonstrateSerializableRead();
    }

    private static void demonstrateDirtyRead() {
        System.out.println("1. READ_UNCOMMITTED 隔離等級 (可能產生髒讀):");

        ExecutorService executor = Executors.newFixedThreadPool(2);

        // 交易 1: 更新但不提交
        executor.submit(() -&gt; {
            try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
                conn.setAutoCommit(false);
                conn.setTransactionIsolation(Connection.TRANSACTION_READ_UNCOMMITTED);

                try (PreparedStatement pstmt = conn.prepareStatement(
                    "UPDATE inventory SET quantity = 100 WHERE id = 1")) {
                    pstmt.executeUpdate();
                    System.out.println("  交易1: 更新庫存為100 (未提交)");

                    Thread.sleep(2000); // 暫停2秒

                    conn.rollback(); // 回滾
                    System.out.println("  交易1: 回滾更新");
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        });

        // 交易 2: 讀取可能未提交的資料
        executor.submit(() -&gt; {
            try {
                Thread.sleep(1000); // 等1秒再讀取

                try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
                    conn.setTransactionIsolation(Connection.TRANSACTION_READ_UNCOMMITTED);

                    try (PreparedStatement pstmt = conn.prepareStatement(
                        "SELECT quantity FROM inventory WHERE id = 1")) {
                        ResultSet rs = pstmt.executeQuery();
                        if (rs.next()) {
                            int quantity = rs.getInt("quantity");
                            System.out.println("  交易2: 讀取到庫存 = " + quantity + " (可能是髒資料)");
                        }
                    }
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        });

        shutdownExecutor(executor);
        System.out.println();
    }

    private static void demonstrateNonRepeatableRead() {
        System.out.println("2. READ_COMMITTED 隔離等級 (可能產生不可重複讀):");

        ExecutorService executor = Executors.newFixedThreadPool(2);

        // 交易 1: 讀取兩次相同資料
        executor.submit(() -&gt; {
            try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
                conn.setAutoCommit(false);
                conn.setTransactionIsolation(Connection.TRANSACTION_READ_COMMITTED);

                // 第一次讀取
                int quantity1 = readQuantity(conn, 2);
                System.out.println("  交易1: 第一次讀取庫存 = " + quantity1);

                Thread.sleep(2000); // 暫停2秒

                // 第二次讀取
                int quantity2 = readQuantity(conn, 2);
                System.out.println("  交易1: 第二次讀取庫存 = " + quantity2);

                if (quantity1 != quantity2) {
                    System.out.println("  ⚠️  不可重複讀取發生！");
                }

                conn.commit();
            } catch (Exception e) {
                e.printStackTrace();
            }
        });

        // 交易 2: 更新資料
        executor.submit(() -&gt; {
            try {
                Thread.sleep(1000); // 等1秒再更新

                try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
                    conn.setAutoCommit(false);

                    try (PreparedStatement pstmt = conn.prepareStatement(
                        "UPDATE inventory SET quantity = 50 WHERE id = 2")) {
                        pstmt.executeUpdate();
                        conn.commit();
                        System.out.println("  交易2: 更新庫存為50並提交");
                    }
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        });

        shutdownExecutor(executor);
        System.out.println();
    }

    private static void demonstratePhantomRead() {
        System.out.println("3. REPEATABLE_READ 隔離等級 (可能產生幻象讀):");
        // 幻象讀示例實作...
        System.out.println("  (示例省略，需要支援範圍查詢的場景)\n");
    }

    private static void demonstrateSerializableRead() {
        System.out.println("4. SERIALIZABLE 隔離等級 (最高隔離等級):");

        ExecutorService executor = Executors.newFixedThreadPool(2);

        // 交易 1
        executor.submit(() -&gt; {
            try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
                conn.setAutoCommit(false);
                conn.setTransactionIsolation(Connection.TRANSACTION_SERIALIZABLE);

                int quantity = readQuantity(conn, 3);
                System.out.println("  交易1: 讀取庫存 = " + quantity);

                Thread.sleep(2000);

                quantity = readQuantity(conn, 3);
                System.out.println("  交易1: 再次讀取庫存 = " + quantity + " (保證一致)");

                conn.commit();
            } catch (Exception e) {
                System.out.println("  交易1: " + e.getMessage());
            }
        });

        // 交易 2
        executor.submit(() -&gt; {
            try {
                Thread.sleep(1000);

                try (Connection conn = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD)) {
                    conn.setAutoCommit(false);
                    conn.setTransactionIsolation(Connection.TRANSACTION_SERIALIZABLE);

                    try (PreparedStatement pstmt = conn.prepareStatement(
                        "UPDATE inventory SET quantity = 75 WHERE id = 3")) {
                        pstmt.executeUpdate();
                        conn.commit();
                        System.out.println("  交易2: 更新庫存為75並提交");
                    }
                }
            } catch (Exception e) {
                System.out.println("  交易2: " + e.getMessage());
            }
        });

        shutdownExecutor(executor);
    }

    private static int readQuantity(Connection conn, int productId) throws SQLException {
        try (PreparedStatement pstmt = conn.prepareStatement(
            "SELECT quantity FROM inventory WHERE id = ?")) {
            pstmt.setInt(1, productId);
            try (ResultSet rs = pstmt.executeQuery()) {
                if (rs.next()) {
                    return rs.getInt("quantity");
                }
                return 0;
            }
        }
    }

    private static void shutdownExecutor(ExecutorService executor) {
        executor.shutdown();
        try {
            executor.awaitTermination(10, TimeUnit.SECONDS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }
}
