        // 使用 super.method() 顯示基本資訊
        super.displayInfo();
        // 添加管理者特有資訊
        System.out.println("管理層級: " + managementLevel);
        System.out.println("團隊人數: " + teamSize);
        System.out.println("績效獎金: $" + String.format("%.2f", bonus));
        System.out.println("總薪水: $" + String.format("%.2f", calculateSalary()));
