        return age == that.age &amp;&amp;
               java.util.Objects.equals(name, that.name) &amp;&amp;
               java.util.Objects.equals(email, that.email);
    }

    @Override
    public int hashCode() {
        return java.util.Objects.hash(name, age, email);
    }

    @Override
    public String toString() {
        return "TraditionalPerson{" +
                "name='" + name + '\'' +
                ", age=" + age +
                ", email='" + email + '\'' +
                '}';
    }
