Java<pre><code>// 泛型類別：自定義容器
public class Box&lt;T&gt; {
    private T content;

    public Box() {
        this.content = null;
    }

    public Box(T content) {
        this.content = content;
    }

    public void set(T content) {
        this.content = content;
    }

    public T get() {
        return content;
    }

    public boolean isEmpty() {
        return content == null;
    }

    public void clear() {
        this.content = null;
    }

    @Override
    public String toString() {
        return "Box{" + (isEmpty() ? "empty" : "content=" + content) + "}";
    }

    // 泛型方法
    public &lt;U&gt; Box&lt;U&gt; map(java.util.function.Function&lt;T, U&gt; mapper) {
        if (isEmpty()) {
            return new Box&lt;&gt;();
        }
        return new Box&lt;&gt;(mapper.apply(content));
    }
}

// 多型別參數的泛型類別
public class Pair&lt;T, U&gt; {
    private T first;
    private U second;

    public Pair(T first, U second) {
        this.first = first;
        this.second = second;
    }

    public T getFirst() { return first; }
    public U getSecond() { return second; }

    public void setFirst(T first) { this.first = first; }
    public void setSecond(U second) { this.second = second; }

    // 交換兩個值（如果類型相同）
    @SuppressWarnings("unchecked")
    public void swap() {
        if (first.getClass().equals(second.getClass())) {
            T temp = first;
            first = (T) second;
            second = (U) temp;
        } else {
            throw new IllegalStateException("無法交換不同類型的值");
        }
    }

    @Override
    public String toString() {
        return String.format("Pair{first=%s, second=%s}", first, second);
    }

    // 靜態泛型方法
    public static &lt;T, U&gt; Pair&lt;T, U&gt; of(T first, U second) {
        return new Pair&lt;&gt;(first, second);
    }
}

// 有界泛型類別（繼承 Number 的類型）
public class NumberBox&lt;T extends Number&gt; {
    private T value;

    public NumberBox(T value) {
        this.value = value;
    }

    public T getValue() { return value; }
    public void setValue(T value) { this.value = value; }

    // 可以呼叫 Number 的方法
    public double getDoubleValue() {
        return value.doubleValue();
    }

    public int getIntValue() {
        return value.intValue();
    }

    // 比較數字大小
    public boolean isGreaterThan(NumberBox&lt;? extends Number&gt; other) {
        return this.getDoubleValue() &gt; other.getDoubleValue();
    }

    // 計算與另一個數字的和
    public double add(NumberBox&lt;? extends Number&gt; other) {
        return this.getDoubleValue() + other.getDoubleValue();
    }

    @Override
    public String toString() {
        return String.format("NumberBox{value=%s, type=%s}", 
                           value, value.getClass().getSimpleName());
    }
}
